import React, { useContext } from "react";
import './cartitems.css';
import { Shopcontext } from "../../context/shopcontext";
import removeicon from "../assets/cart_cross_icon.png";
const Cartiems=()=>{
    const {all_product,cartiem,removefromcart}=useContext(Shopcontext);
    return(
        <div className="cartitems">
            <div className="cartitemmen">
                <p>Products</p>
                <p>Title</p>
                <p>Price</p>
                <p>Qty</p>
                <p>Total</p>
                <p>Remove</p>
            </div>
            <hr/>
            {all_product.map((e)=>{
                if(cartiem[e.id]>0){
                    <div>
                <div className="cartitemsformat">
                    <img src={e.image} alt="" className="carticon" />
                    <p></p>
                    <p></p>
                    <button className="cartitemsqty"></button>
                    <p></p>
                    <img src={removeicon} onClick={()=>{removefromcart()}} alt="" />
                </div>
                <hr/>
            </div>
                }
            })}
        </div>
    )
}

export default Cartiems;
